/** * $Id: WangYiOssProperties.java,v 1.0 2022/3/4 9:51 PM chenmin Exp $ */package io.github.artislong.core.wangyi;import cn.hutool.core.util.StrUtil;import io.github.artislong.constant.OssConstant;import io.github.artislong.core.tencent.model.TencentOssConfig;import io.github.artislong.core.wangyi.model.WangYiOssConfig;import lombok.Data;import org.springframework.beans.factory.InitializingBean;import org.springframework.boot.context.properties.ConfigurationProperties;import java.util.HashMap;import java.util.Map;/** * @author 陈敏 * @version $Id: WangYiOssProperties.java,v 1.1 2022/3/4 9:51 PM chenmin Exp $ * Created on 2022/3/4 9:51 PM * My blog： https://www.chenmin.info */@Data@ConfigurationProperties(OssConstant.OSS + StrUtil.DOT + OssConstant.OssType.WANGYI)public class WangYiOssProperties extends WangYiOssConfig implements InitializingBean {    private Boolean enable = false;    private Map<String, WangYiOssConfig> ossConfig = new HashMap<>();    @Override    public void afterPropertiesSet() {        if (ossConfig.isEmpty()) {            this.init();        } else {            ossConfig.values().forEach(WangYiOssConfig::init);        }    }}